import{_ as t,c as i,o as a,ah as o}from"./chunks/framework.D--hi4lz.js";const c=JSON.parse('{"title":"User Guide","description":"","frontmatter":{},"headers":[],"relativePath":"USER_GUIDE.md","filePath":"USER_GUIDE.md"}'),n={name:"USER_GUIDE.md"};function r(l,e,s,u,h,g){return a(),i("div",null,[...e[0]||(e[0]=[o('<h1 id="user-guide" tabindex="-1">User Guide <a class="header-anchor" href="#user-guide" aria-label="Permalink to &quot;User Guide&quot;">​</a></h1><h2 id="table-of-contents" tabindex="-1">Table of Contents <a class="header-anchor" href="#table-of-contents" aria-label="Permalink to &quot;Table of Contents&quot;">​</a></h2><ol><li><a href="#what-is-thaiheavenssignapp">What is ThaiHeavensSignApp?</a></li><li><a href="#for-administrators">For Administrators</a></li><li><a href="#for-guests-signing-a-contract">For Guests (Signing a Contract)</a></li><li><a href="#troubleshooting">Troubleshooting</a></li></ol><hr><h2 id="what-is-thaiheavenssignapp" tabindex="-1">What is ThaiHeavensSignApp? <a class="header-anchor" href="#what-is-thaiheavenssignapp" aria-label="Permalink to &quot;What is ThaiHeavensSignApp?&quot;">​</a></h2><p>ThaiHeavensSignApp is a web-based system that allows you to:</p><ul><li>Upload rental contracts (PDF files)</li><li>Create secure signing links for guests</li><li>Allow guests to sign contracts digitally using their finger or mouse</li><li>Download the final signed contracts</li></ul><p><strong>No special software needed</strong> - everything works in your web browser!</p><hr><h2 id="for-administrators" tabindex="-1">For Administrators <a class="header-anchor" href="#for-administrators" aria-label="Permalink to &quot;For Administrators&quot;">​</a></h2><h3 id="getting-started" tabindex="-1">Getting Started <a class="header-anchor" href="#getting-started" aria-label="Permalink to &quot;Getting Started&quot;">​</a></h3><ol><li>Open the admin page in your web browser</li><li>You&#39;ll see a list of all signing sessions (if any exist)</li></ol><h3 id="step-1-upload-a-pdf-contract" tabindex="-1">Step 1: Upload a PDF Contract <a class="header-anchor" href="#step-1-upload-a-pdf-contract" aria-label="Permalink to &quot;Step 1: Upload a PDF Contract&quot;">​</a></h3><ol><li>Click the <strong>&quot;New Session&quot;</strong> button</li><li>You&#39;ll see an upload area</li><li><strong>Option A - Drag and Drop:</strong><ul><li>Drag your PDF file from your computer</li><li>Drop it into the upload area</li><li>The file will upload automatically</li></ul></li><li><strong>Option B - File Picker:</strong><ul><li>Click the upload area or &quot;Choose File&quot; button</li><li>Select your PDF file from your computer</li><li>The file will upload automatically</li></ul></li></ol><p><strong>Note:</strong> Only PDF files are supported.</p><h3 id="step-2-select-signature-position" tabindex="-1">Step 2: Select Signature Position <a class="header-anchor" href="#step-2-select-signature-position" aria-label="Permalink to &quot;Step 2: Select Signature Position&quot;">​</a></h3><p>After uploading, you&#39;ll see your PDF displayed:</p><ol><li><p><strong>Navigate Pages:</strong></p><ul><li>Use the &quot;Previous&quot; and &quot;Next&quot; buttons to find the page where the signature should appear</li></ul></li><li><p><strong>Position the Signature Box:</strong></p><ul><li><strong>Click</strong> anywhere on the PDF where you want the signature to appear</li><li>A red rectangle will appear showing where the signature will be placed</li><li><strong>To move it:</strong> Click and drag the red rectangle to a new position</li><li><strong>To resize it:</strong> Click and drag one of the red corner dots <ul><li>The box will maintain its proportions automatically</li></ul></li></ul></li><li><p><strong>Fine-tune (Optional):</strong></p><ul><li>You can use the <strong>&quot;Calibrate&quot;</strong> tool (available from the main menu) to test signature placement</li><li>This helps ensure signatures appear exactly where you want them</li></ul></li></ol><h3 id="step-3-create-signing-link" tabindex="-1">Step 3: Create Signing Link <a class="header-anchor" href="#step-3-create-signing-link" aria-label="Permalink to &quot;Step 3: Create Signing Link&quot;">​</a></h3><ol><li>After positioning the signature box, click <strong>&quot;Continue&quot;</strong></li><li>Fill in the guest information: <ul><li><strong>Guest Name:</strong> Enter the guest&#39;s name (optional but recommended)</li><li><strong>Guest Email:</strong> Enter the guest&#39;s email (optional)</li></ul></li><li>Click <strong>&quot;Create Signing Link&quot;</strong></li><li>A signing link will be generated</li><li><strong>Copy the link</strong> and send it to your guest via email, text message, or any other method</li></ol><p><strong>Important:</strong> Keep this link secure - anyone with the link can sign the contract!</p><h3 id="managing-sessions" tabindex="-1">Managing Sessions <a class="header-anchor" href="#managing-sessions" aria-label="Permalink to &quot;Managing Sessions&quot;">​</a></h3><p>On the main admin page, you can:</p><ul><li><p><strong>View All Sessions:</strong></p><ul><li>See all signing sessions you&#39;ve created</li><li>View status: Pending, Signed, or Completed</li></ul></li><li><p><strong>Copy Signing Link:</strong></p><ul><li>Click the <strong>&quot;Copy URL&quot;</strong> button next to any session</li><li>The link is copied to your clipboard</li><li>Send it to your guest</li></ul></li><li><p><strong>Download Signed PDF:</strong></p><ul><li>Once a guest has signed and confirmed, a <strong>&quot;Download&quot;</strong> button appears</li><li>Click it to download the final signed contract</li></ul></li><li><p><strong>Delete Session:</strong></p><ul><li>Click the <strong>&quot;Delete&quot;</strong> button to remove a session</li><li>This permanently deletes the session and associated files</li><li><strong>Warning:</strong> This action cannot be undone!</li></ul></li></ul><hr><h2 id="for-guests-signing-a-contract" tabindex="-1">For Guests (Signing a Contract) <a class="header-anchor" href="#for-guests-signing-a-contract" aria-label="Permalink to &quot;For Guests (Signing a Contract)&quot;">​</a></h2><h3 id="receiving-the-signing-link" tabindex="-1">Receiving the Signing Link <a class="header-anchor" href="#receiving-the-signing-link" aria-label="Permalink to &quot;Receiving the Signing Link&quot;">​</a></h3><p>You&#39;ll receive a link from the administrator (via email, text, etc.). The link looks like:</p><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>https://yoursite.com/sign/abc123xyz</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h3 id="step-1-open-the-link" tabindex="-1">Step 1: Open the Link <a class="header-anchor" href="#step-1-open-the-link" aria-label="Permalink to &quot;Step 1: Open the Link&quot;">​</a></h3><ol><li>Click the link or paste it into your web browser</li><li>The contract will load automatically</li><li>You&#39;ll see a welcome message with your name (if provided)</li></ol><h3 id="step-2-review-the-contract" tabindex="-1">Step 2: Review the Contract <a class="header-anchor" href="#step-2-review-the-contract" aria-label="Permalink to &quot;Step 2: Review the Contract&quot;">​</a></h3><ol><li><strong>Read the contract carefully</strong></li><li>Use the <strong>&quot;Previous&quot;</strong> and <strong>&quot;Next&quot;</strong> buttons to navigate through all pages</li><li>Make sure you understand everything before signing</li></ol><h3 id="step-3-draw-your-signature" tabindex="-1">Step 3: Draw Your Signature <a class="header-anchor" href="#step-3-draw-your-signature" aria-label="Permalink to &quot;Step 3: Draw Your Signature&quot;">​</a></h3><ol><li>Scroll down to the signature area</li><li>You&#39;ll see a white box labeled <strong>&quot;SIGNATURE CANVAS&quot;</strong></li><li><strong>On a computer:</strong><ul><li>Use your mouse to draw your signature</li></ul></li><li><strong>On a phone or tablet:</strong><ul><li>Use your finger to draw your signature</li></ul></li><li>There&#39;s a light gray line to help you align your signature</li><li><strong>To start over:</strong> Click the <strong>&quot;Clear Signature&quot;</strong> button</li></ol><h3 id="step-4-apply-your-signature" tabindex="-1">Step 4: Apply Your Signature <a class="header-anchor" href="#step-4-apply-your-signature" aria-label="Permalink to &quot;Step 4: Apply Your Signature&quot;">​</a></h3><ol><li>Once you&#39;re happy with your signature, click <strong>&quot;Apply Signature&quot;</strong></li><li>The system will process your signature</li><li>You&#39;ll see a preview of the contract with your signature in place</li></ol><h3 id="step-5-review-and-confirm" tabindex="-1">Step 5: Review and Confirm <a class="header-anchor" href="#step-5-review-and-confirm" aria-label="Permalink to &quot;Step 5: Review and Confirm&quot;">​</a></h3><ol><li><p><strong>Review the preview:</strong></p><ul><li>Check that your signature appears in the correct location</li><li>Make sure it looks good</li></ul></li><li><p><strong>If you&#39;re satisfied:</strong></p><ul><li>Click <strong>&quot;✓ Confirm &amp; Download&quot;</strong></li><li>Your signature is finalized</li><li>The signed PDF will download automatically</li><li>A confirmation message will appear</li></ul></li><li><p><strong>If you want to change your signature:</strong></p><ul><li>Click <strong>&quot;Clear &amp; Sign Again&quot;</strong></li><li>You&#39;ll return to the signature drawing step</li><li>Draw a new signature and repeat the process</li></ul></li></ol><h3 id="after-confirming" tabindex="-1">After Confirming <a class="header-anchor" href="#after-confirming" aria-label="Permalink to &quot;After Confirming&quot;">​</a></h3><ul><li>Your signature has been recorded</li><li>The administrator can now download the final signed contract</li><li>You should keep a copy of the downloaded PDF for your records</li></ul><hr><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h2><h3 id="this-signing-link-is-not-valid-or-has-expired" tabindex="-1">&quot;This signing link is not valid or has expired&quot; <a class="header-anchor" href="#this-signing-link-is-not-valid-or-has-expired" aria-label="Permalink to &quot;&quot;This signing link is not valid or has expired&quot;&quot;">​</a></h3><p><strong>What it means:</strong> The link you&#39;re trying to use doesn&#39;t exist or has been deleted.</p><p><strong>What to do:</strong></p><ul><li>Contact the administrator to get a new signing link</li><li>Make sure you copied the entire link correctly</li></ul><h3 id="failed-to-load-pdf" tabindex="-1">&quot;Failed to load PDF&quot; <a class="header-anchor" href="#failed-to-load-pdf" aria-label="Permalink to &quot;&quot;Failed to load PDF&quot;&quot;">​</a></h3><p><strong>What it means:</strong> The PDF file couldn&#39;t be loaded.</p><p><strong>What to do:</strong></p><ul><li>Check your internet connection</li><li>Try refreshing the page</li><li>Contact the administrator if the problem persists</li></ul><h3 id="please-draw-your-signature-first" tabindex="-1">&quot;Please draw your signature first&quot; <a class="header-anchor" href="#please-draw-your-signature-first" aria-label="Permalink to &quot;&quot;Please draw your signature first&quot;&quot;">​</a></h3><p><strong>What it means:</strong> You tried to apply a signature without drawing one.</p><p><strong>What to do:</strong></p><ul><li>Draw your signature in the signature canvas</li><li>Make sure you can see your signature before clicking &quot;Apply Signature&quot;</li></ul><h3 id="signature-doesn-t-appear-in-the-preview" tabindex="-1">Signature doesn&#39;t appear in the preview <a class="header-anchor" href="#signature-doesn-t-appear-in-the-preview" aria-label="Permalink to &quot;Signature doesn&#39;t appear in the preview&quot;">​</a></h3><p><strong>What it means:</strong> There might be an issue with the signature image.</p><p><strong>What to do:</strong></p><ul><li>Try drawing your signature again</li><li>Make sure you&#39;re drawing on the white canvas area</li><li>Click &quot;Clear Signature&quot; and start over</li></ul><h3 id="can-t-see-the-signature-box-on-mobile" tabindex="-1">Can&#39;t see the signature box on mobile <a class="header-anchor" href="#can-t-see-the-signature-box-on-mobile" aria-label="Permalink to &quot;Can&#39;t see the signature box on mobile&quot;">​</a></h3><p><strong>What it means:</strong> The screen might be too small or zoomed in.</p><p><strong>What to do:</strong></p><ul><li>Zoom out on your mobile browser</li><li>Rotate your device to landscape mode</li><li>Try scrolling to see the signature area</li></ul><h3 id="pdf-won-t-load" tabindex="-1">PDF won&#39;t load <a class="header-anchor" href="#pdf-won-t-load" aria-label="Permalink to &quot;PDF won&#39;t load&quot;">​</a></h3><p><strong>What it means:</strong> There might be an issue with the PDF file or your browser.</p><p><strong>What to do:</strong></p><ul><li>Try a different web browser (Chrome, Firefox, Safari, Edge)</li><li>Make sure your browser is up to date</li><li>Check your internet connection</li><li>Contact the administrator</li></ul><h3 id="can-t-download-the-signed-pdf" tabindex="-1">Can&#39;t download the signed PDF <a class="header-anchor" href="#can-t-download-the-signed-pdf" aria-label="Permalink to &quot;Can&#39;t download the signed PDF&quot;">​</a></h3><p><strong>What it means:</strong> The download might be blocked by your browser or the file isn&#39;t ready.</p><p><strong>What to do:</strong></p><ul><li>Check your browser&#39;s download settings</li><li>Make sure pop-ups aren&#39;t blocked</li><li>Try right-clicking the download button and selecting &quot;Save link as...&quot;</li><li>Contact the administrator if the issue persists</li></ul><hr><h2 id="tips-for-best-results" tabindex="-1">Tips for Best Results <a class="header-anchor" href="#tips-for-best-results" aria-label="Permalink to &quot;Tips for Best Results&quot;">​</a></h2><h3 id="for-administrators-1" tabindex="-1">For Administrators: <a class="header-anchor" href="#for-administrators-1" aria-label="Permalink to &quot;For Administrators:&quot;">​</a></h3><ol><li><strong>Test the signing link yourself</strong> before sending it to guests</li><li><strong>Position the signature box carefully</strong> - use the calibration tool if needed</li><li><strong>Keep signing links secure</strong> - don&#39;t share them publicly</li><li><strong>Download signed PDFs promptly</strong> - keep backups of important contracts</li></ol><h3 id="for-guests" tabindex="-1">For Guests: <a class="header-anchor" href="#for-guests" aria-label="Permalink to &quot;For Guests:&quot;">​</a></h3><ol><li><strong>Use a stable internet connection</strong> when signing</li><li><strong>Take your time</strong> - read the contract thoroughly</li><li><strong>Draw your signature clearly</strong> - make sure it&#39;s readable</li><li><strong>Review the preview</strong> before confirming</li><li><strong>Save the downloaded PDF</strong> for your records</li><li><strong>Use a modern browser</strong> for best compatibility</li></ol><hr><h2 id="frequently-asked-questions" tabindex="-1">Frequently Asked Questions <a class="header-anchor" href="#frequently-asked-questions" aria-label="Permalink to &quot;Frequently Asked Questions&quot;">​</a></h2><h3 id="q-do-i-need-to-install-any-software" tabindex="-1">Q: Do I need to install any software? <a class="header-anchor" href="#q-do-i-need-to-install-any-software" aria-label="Permalink to &quot;Q: Do I need to install any software?&quot;">​</a></h3><p><strong>A:</strong> No! Everything works in your web browser. No downloads or installations needed.</p><h3 id="q-is-my-signature-secure" tabindex="-1">Q: Is my signature secure? <a class="header-anchor" href="#q-is-my-signature-secure" aria-label="Permalink to &quot;Q: Is my signature secure?&quot;">​</a></h3><p><strong>A:</strong> Yes. Your signature is embedded directly into the PDF and cannot be easily removed or altered.</p><h3 id="q-can-i-sign-on-my-phone" tabindex="-1">Q: Can I sign on my phone? <a class="header-anchor" href="#q-can-i-sign-on-my-phone" aria-label="Permalink to &quot;Q: Can I sign on my phone?&quot;">​</a></h3><p><strong>A:</strong> Yes! The system is designed to work on phones, tablets, and computers.</p><h3 id="q-what-if-i-make-a-mistake-while-signing" tabindex="-1">Q: What if I make a mistake while signing? <a class="header-anchor" href="#q-what-if-i-make-a-mistake-while-signing" aria-label="Permalink to &quot;Q: What if I make a mistake while signing?&quot;">​</a></h3><p><strong>A:</strong> You can click &quot;Clear Signature&quot; to start over, or &quot;Clear &amp; Sign Again&quot; after applying to redo your signature.</p><h3 id="q-can-i-sign-multiple-contracts" tabindex="-1">Q: Can I sign multiple contracts? <a class="header-anchor" href="#q-can-i-sign-multiple-contracts" aria-label="Permalink to &quot;Q: Can I sign multiple contracts?&quot;">​</a></h3><p><strong>A:</strong> Each signing link is for one contract. If you need to sign multiple contracts, you&#39;ll receive separate links.</p><h3 id="q-how-long-is-the-signing-link-valid" tabindex="-1">Q: How long is the signing link valid? <a class="header-anchor" href="#q-how-long-is-the-signing-link-valid" aria-label="Permalink to &quot;Q: How long is the signing link valid?&quot;">​</a></h3><p><strong>A:</strong> The link remains valid until the administrator deletes the session or you complete the signing process.</p><h3 id="q-can-i-print-the-signed-contract" tabindex="-1">Q: Can I print the signed contract? <a class="header-anchor" href="#q-can-i-print-the-signed-contract" aria-label="Permalink to &quot;Q: Can I print the signed contract?&quot;">​</a></h3><p><strong>A:</strong> Yes! After downloading, you can print the PDF like any other document.</p><h3 id="q-what-file-format-is-the-signed-contract" tabindex="-1">Q: What file format is the signed contract? <a class="header-anchor" href="#q-what-file-format-is-the-signed-contract" aria-label="Permalink to &quot;Q: What file format is the signed contract?&quot;">​</a></h3><p><strong>A:</strong> The signed contract is a standard PDF file that can be opened in any PDF viewer.</p><hr><h2 id="need-help" tabindex="-1">Need Help? <a class="header-anchor" href="#need-help" aria-label="Permalink to &quot;Need Help?&quot;">​</a></h2><p>If you encounter any issues not covered in this guide:</p><ol><li><strong>Check the Troubleshooting section</strong> above</li><li><strong>Contact the administrator</strong> who sent you the signing link</li><li><strong>Try using a different device or browser</strong></li></ol><hr><h2 id="screenshots-placeholders" tabindex="-1">Screenshots Placeholders <a class="header-anchor" href="#screenshots-placeholders" aria-label="Permalink to &quot;Screenshots Placeholders&quot;">​</a></h2><p><em>Note: In a complete version of this guide, screenshots would be included here showing:</em></p><ol><li><p><strong>Admin Interface:</strong></p><ul><li>Main sessions list page</li><li>PDF upload area</li><li>PDF with signature box positioned</li><li>Session creation form</li><li>Generated signing link</li></ul></li><li><p><strong>Guest Interface:</strong></p><ul><li>Contract viewing page</li><li>Signature drawing canvas</li><li>Preview of signed contract</li><li>Confirmation page</li></ul></li><li><p><strong>Mobile Views:</strong></p><ul><li>How the interface looks on phones</li><li>Touch-friendly signature drawing</li></ul></li></ol><p><em>These visual guides help users understand each step of the process.</em></p>',104)])])}const p=t(n,[["render",r]]);export{c as __pageData,p as default};
